/* Copy Pre Content */
for (let pre = document.getElementsByTagName("pre"), i = 0; i < pre.length; i++) {
  pre[i].addEventListener("dblclick", function () {
    var e = getSelection(),
      o = document.createRange();
    o.selectNodeContents(this);
    e.removeAllRanges();
    e.addRange(o);
    document.execCommand("copy");
    e.removeAllRanges();
    toastNotif(PuSet.preCopy && PuSet.preCopy.copiedMes ? PuSet.preCopy.copiedMes : 'تم النسخ الى الحافظة!');
    vibRate(75)
  }, !1)
}